<?php//Процесс создания контакта закончен, приступаем к созданию задачи. $dedlain=$time+7200;$tasks['request']['tasks']['add']=array(#Привязываем к сделке array(  'element_id'=>$id_sdelki, #ID сделки  'element_type'=>2, #Показываем, что это - сделка, а не контакт  'task_type'=>1, #Звонок  'text'=>'Позвонить',  'responsible_user_id'=>$account['user'][0],  'complete_till'=>$dedlain ),);$link='https://'.$subdomain.'.amocrm.ru/private/api/v2/json/tasks/set';$curl=curl_init(); #Сохраняем дескриптор сеанса cURLcurl_setopt($curl,CURLOPT_RETURNTRANSFER,true);curl_setopt($curl,CURLOPT_USERAGENT,'amoCRM-API-client/1.0');curl_setopt($curl,CURLOPT_URL,$link);curl_setopt($curl,CURLOPT_CUSTOMREQUEST,'POST');curl_setopt($curl,CURLOPT_POSTFIELDS,json_encode($tasks));curl_setopt($curl,CURLOPT_HTTPHEADER,array('Content-Type: application/json'));curl_setopt($curl,CURLOPT_HEADER,false);curl_setopt($curl,CURLOPT_COOKIEFILE,dirname(__FILE__).'/cookie.txt'); #PHP>5.3.6 dirname(__FILE__) -> __DIR__curl_setopt($curl,CURLOPT_COOKIEJAR,dirname(__FILE__).'/cookie.txt'); #PHP>5.3.6 dirname(__FILE__) -> __DIR__curl_setopt($curl,CURLOPT_SSL_VERIFYPEER,0);curl_setopt($curl,CURLOPT_SSL_VERIFYHOST,0);   $out=curl_exec($curl); #Инициируем запрос к API и сохраняем ответ в переменную	$code=curl_getinfo($curl,CURLINFO_HTTP_CODE);$Response=json_decode($out,true);$Response=$Response['response']['tasks']['add']; ?>